async function copyToClipboard(t){try{await navigator.clipboard.writeText(t),alert("Text copied to clipboard: "+t)}catch(t){alert("Failed to copy text: "+t)}}document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[data-clipboard]").forEach(e=>{e.addEventListener("click",t=>{t.preventDefault(),copyToClipboard(e.getAttribute("data-clipboard"))})})});